public class Exo6_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] nb = new int[6];
		int i;

		for (i = 0; i <= 5; i++) {
			nb[i] = i * i;
			System.out.println(nb[i]);

		}

	}

}

